//20.Karma Royal Sanur - Bali
$id="1169";
$bannerImages=array("https://karmagroup.com/wp-content/uploads/2016/10/Karma-Royal-Sanur-Headline-a.jpg","https://karmagroup.com/wp-content/uploads/2016/10/Karma-Royal-Sanur-Headline-b.jpg","https://karmagroup.com/wp-content/uploads/2016/10/Karma-Royal-Sanur-Headline-c.jpg","https://karmagroup.com/wp-content/uploads/2016/10/Karma-Royal-Sanur-Headline-d.jpg","https://karmagroup.com/wp-content/uploads/2016/10/Karma-Royal-Sanur-Headline-e.jpg");
$title = "Karma Royal Sanur, Sanur, Bali";
