function saveAmenities($connection,$postID,$amenities)
{
	for($i=0;$i<count($amenities);$i++)
	{
		$option1 = isset($amenities[$i][3])?$amenities[$i][3]:"";
		$option2 = isset($amenities[$i][4])?$amenities[$i][4]:"";
		//saving in amenities
		$query = "insert into amenities(postID,name,description,image,option1,option2) values(".$postID.",'".mysqli_real_escape_string($connection,$amenities[$i][0])."','".mysqli_real_escape_string($connection,$amenities[$i][1])."','".$amenities[$i][2]."','".$option1."','".$option2."')";
		$cmd = mysqli_query($connection,$query);
	}
}

function saveAccomodation($connection,$postID,$accomodation)
{
	for($i=0;$i<count($accomodation);$i++)
	{
		//saving in accomodation
		$query = "insert into accomodation(postID,name,image,description) values(".$postID.",'".mysqli_real_escape_string($connection,$accomodation[$i][0])."','".$accomodation[$i][1]."','".mysqli_real_escape_string($connection,$accomodation[$i][2])."')";
		$cmd = mysqli_query($connection,$query);
	}
}

function saveDestinationImages($connection,$postID,$destinationImages)
{
	for($i=0;$i<count($destinationImages);$i++)
	{
		//saving in destinationImages
		$query = "insert into destinationImages(postID,image) values(".$postID.",'".$destinationImages[$i]."')";
		$cmd = mysqli_query($connection,$query);
	}
